-- PostgreSQL version of invoice_pay_srv table and data
DROP TABLE IF EXISTS invoice_pay_srv;
CREATE TABLE invoice_pay_srv (
    no INTEGER PRIMARY KEY,
    pkt_inv VARCHAR(50),
    inv_tag VARCHAR(50),
    inv_pay VARCHAR(100),
    tgl_pay DATE,
    id_cst VARCHAR(30),
    nama_cst VARCHAR(100),
    jml_tagihan VARCHAR(30),
    jml_pay VARCHAR(30),
    sisa_pay VARCHAR(30),
    type_pay VARCHAR(30),
    ket_pay VARCHAR(255),
    admin VARCHAR(50)
);
TRUNCATE TABLE invoice_pay_srv RESTART IDENTITY;
